import React from "react";
import { DateConverter } from "./DateConverter";

const CardDetails = ({ post, onClose }) => {
  return (
    <div className="card-details">
      <div className="btn">
        <button className="close-btn" onClick={onClose}>
          Ã—
        </button>
      </div>
      <div className="img-box">
        <img
          src={post.thumbnail?.small}
          alt="small"
        />
      </div>
      <div className="details">
        <h2 className="title">{post.title}</h2>
        <p className="bio">{post.content}</p>
      </div>
      <div className="publish-details">
        <div className="left">
          {post.author && (
            <>
              <img className="author" src={post.author.avatar} alt="author" />
              <span className="author">{post.author.name}</span>
              <span className="role">{post.author.role}</span>
            </>
          )}
        </div>
        <div className="right">
          <span className="date">{DateConverter(post.date)}</span>
        </div>
      </div>
    </div>
  );
};

export default CardDetails;
