import React from "react";
import { DateConverter } from "./DateConverter";
const Card = ({ post, onLearnMore }) => {
  return (
    <div className="card">
      <div className="img-box">
        <img src={post.thumbnail?.small} alt="Image" />
      </div>
      <span className="learn-more" onClick={() => onLearnMore()}>
        Learn More
      </span>
      <div className="details">
        <h2 className="title">{post.title}</h2>
        <p className="bio">{post.content}</p>
      </div>
      <div className="publish-details">
        <div className="left">
          <span className="author">{post.author.name}</span>
          <span className="role">{post.author.role}</span>
        </div>
        <div className="right">
          <span className="date">{DateConverter(post.date)}</span>
        </div>
      </div>
    </div>
  );
};

export default Card;
